WorkflowProcessEvent.eventDescription:
function afterProcessFinish(processId){
	
	log.info("===============PROCESSO=======================");
	
	log.info(processId);
	
	log.info("===============DESVIO=======================");
	
	log.info(hAPI.getCardValue("cod_desvio"));
	
	log.info("===============STATUS=======================");
	
	log.info(hAPI.getCardValue("ind_status_desvio"));
	
	var str = hAPI.getCardValue("cod_estabel");
	var estab = str.substring(0, 3); 
	
	log.info(estab);
	
	var objService = ServiceManager.getService('Atualiza');

    var serviceHelper = objService.getBean();

    var serviceLocator = serviceHelper.instantiate('tempuri_org.WsfluigintetstServiceLocator');
    
    var service = serviceLocator.getwsfluigintetstObj();
    
    var soapEnv = "<?xml version='1.0' encoding='UTF-8'?><Desvio versao='1.07'><id_desvio>" + hAPI.getCardValue("cod_desvio") + "</id_desvio><estabelecimento>" + estab.trim() +  "</estabelecimento><status>" + hAPI.getCardValue("ind_status_desvio") + "</status></Desvio>"     
    
    var respEnv = new javax.xml.rpc.holders.StringHolder("");
    	
    service.esdes001(soapEnv, respEnv);

    log.info(respEnv.value);
 
}

function beforeTaskSave(colleagueId,nextSequenceId,userList){
	var WKNumProces = getValue("WKNumProces");
	var WKUser = getValue("WKUser");	

	if(nextSequenceId == '233'){
		var dataset = DatasetFactory.getDataset('ds_consulta_param_aprov', null, null, null);
		if(!dataset)
			throw "Não há aprovadores cadastrados!";
		
		if(dataset.rowsCount < 1)
			throw "Não há aprovadores cadastrados!";
	}
	
	if(nextSequenceId == '238'){
		var dataset = DatasetFactory.getDataset('ds_consulta_param_aprov', null, null, null);		

		var cont = dataset.rowsCount - 1;
		var cont_aprovador = parseInt(hAPI.getCardValue('cont_aprovador'));

		if(cont_aprovador < cont){
			var ctap = cont_aprovador + 1;

			var aprovador = 'Pool:Role:' + dataset.getValue(ctap,'cod_papel');
			var descAprovador = dataset.getValue(ctap,'desc_papel');

			hAPI.setCardValue('cod_aprovador',aprovador);
			hAPI.setCardValue('cod_aprovador',aprovador);
			hAPI.setCardValue('cont_aprovador',ctap);
			hAPI.setCardValue('envia_aprovador','sim');			

			hAPI.setTaskComments(WKUser, WKNumProces,  0, "Encaminhando para a aprovação do papel " + descAprovador);

		} else {
			hAPI.setCardValue('envia_aprovador','nao');
		}
	}
	
	if(nextSequenceId == '122'){
		hAPI.setTaskComments(WKUser, WKNumProces,  0, "Solicitação aprovada!");
	}
}

function afterCancelProcess(colleagueId,processId){
log.info("===============PROCESSO=======================");
	
	log.info(processId);
	
	log.info("===============DESVIO=======================");
	
	log.info(hAPI.getCardValue("cod_desvio"));
	
	log.info("===============STATUS=======================");
	
	log.info(hAPI.getCardValue("ind_status_desvio"));
	
	var str = hAPI.getCardValue("cod_estabel");
	var estab = str.substring(0, 3); 
	
	log.info(estab);
	
	var objService = ServiceManager.getService('Atualiza');

    var serviceHelper = objService.getBean();

    var serviceLocator = serviceHelper.instantiate('tempuri_org.WsfluigintetstServiceLocator');
    
    var service = serviceLocator.getwsfluigintetstObj();
    
    var SQL = "select DSL_OBS_TAR from tar_proces where NUM_PROCES = '"+processId+"' and IDI_STATUS = 4";
	var c1 = DatasetFactory.createConstraint("SQL", SQL, SQL, ConstraintType.MUST);
	var dsState = DatasetFactory.getDataset('ds_consultaDB', null, [c1], null);
    
	var obsCancel = "";
	
	if(dsState){
		if(dsState.rowsCount() > 0){
			obsCancel = dsState.getValue("DSL_OBS_TAR");
		}
	}     
	var soapEnv = "<?xml version='1.0' encoding='UTF-8'?><Desvio versao='1.07'><id_desvio>" + hAPI.getCardValue("cod_desvio") + "</id_desvio><estabelecimento>" + estab.trim() +  "</estabelecimento><status>8</status><observacao>" + obsCancel + "</observacao></Desvio>";
    
    var respEnv = new javax.xml.rpc.holders.StringHolder("");
    	
    service.esdes001(soapEnv, respEnv);

    log.info(respEnv.value);
}

function servicetask233(attempt, message) {
	var WKNumProces = getValue("WKNumProces");
	var WKUser = getValue("WKUser");

	var dataset = DatasetFactory.getDataset('ds_consulta_param_aprov', null, null, null);		

	var aprovador = 'Pool:Role:' + dataset.getValue(0,'cod_papel');
	var descAprovador = dataset.getValue(0,'desc_papel');

	try {
		hAPI.setCardValue('cod_aprovador',aprovador);
		hAPI.setCardValue('cont_aprovador','0');

		hAPI.setTaskComments(WKUser, WKNumProces,  0, "Encaminhando para a aprovação do papel " + descAprovador);

	} catch(error) { 
		hAPI.setCardValue('cod_aprovador',aprovador);
		hAPI.setCardValue('cont_aprovador','0');

		hAPI.setTaskComments(WKUser, WKNumProces,  0, "Encaminhando para a aprovação do papel " + descAprovador);
	}
}
